import React, { Component } from 'react';
import {WIDTH, HEIGHT, COLORS} from './constants'


export default class BoxCanvas extends Component{

    ctx = canv.getContext('2d')

    componentWillMount(){
        this.drawBody()
    }

    drawBody = () => ctx.fillStyle = COLORS.body

    render(){
        var canv = this.refs.canvas; 
        canv.width = WIDTH;
        canv.heigth = HEIGHT;

        return(
            <div>
                <canvas ref='canvas' className='canvasBody'>

                </canvas>            

            </div>
        )
    }
}
