import React, { Component } from 'react';
import {WIDTH, HEIGHT, COLORS} from './constants'


export default class BoxCanvas extends Component{

    // canv = this.refs.canvas; 
    // ctx = this.canv.getContext('2d');

    componentDidlMount(){
        this.updateCanvas()
    }

    componentDidUpdate(){
        this.updateCanvas()
    }

    updateCanvas = () => {
        const ctx = this.refs.canvas.getContext('2d');
        this.refs.canvas.width = WIDTH;
        this.refs.canvas.heigth = HEIGHT;

        this.drawBody()
    }

    drawBody = () => this.ctx.fillStyle = COLORS.body

    render(){   


        return(
            <div>
                <canvas ref='canvas' className='canvasBody'>

                </canvas>            

            </div>
        )
    }
}
