import React, { Component } from 'react';
// import Konva from 'konva';
import { Line } from 'react-konva';

export default class SideLine extends Component{
    state = {
        color: '',
        fixed: false,
        strokeWidth: 20,
        opacity: 0
    }

    componentWillMount(){
        this.setState({
            color: this.props.color,
            opacity: 0
        })
    }

    handleMouseOver = () => {
        if (!this.state.fixed)  return this.setState({strokeWidth: 20, color: 'blue', opacity: 1})
    }

    handleMouseOut = () => {
        if (!this.state.fixed)  return this.setState({strokeWidth: 25, color: this.props.color})  
    }

    handleClick = () => this.setState ({color: 'green', fixed: true})

    render(){   
        const {index, x0, y0, x1, y1} = this.props
        return(
            <Line
                key = {index}
                points ={[x0, y0, x1, y1]}
                tension ={-0.5}  
                stroke = {this.state.color}
                shadowBlur ={2}
                strokeWidth ={this.state.strokeWidth}
                onMouseOver = {this.handleMouseOver}
                onMouseOut = {this.handleMouseOut}
                onClick = {this.handleClick}
                opacity = {this.state.opacitys}
            /> 
        )
    }
}
