import React, { Component } from 'react';
import {WIDTH, HEIGHT} from './constants'
import { Stage, Layer, Rect, Circle } from 'react-konva';
import Konva from 'konva';

class Box{
    constructor(x, y, w, h){
        this.w = w;
        this.y = y;
        this.left = x;
        this.top = y;
        this.right = x + w;
        this.bottom = y + h;

        this.contains = function(x, y){
            return x >= this.left && x < this.right && y >= this.top && y < this.bottom;
        }
    }       
}

export default class BoxCanvas extends Component{

    state = {
        color: 'grey',
        row: 4,
        colomn: 6,
        marginWidth: 0,
        marginHeight: 0
    };

    componentWillMount(){
        this.setState({
            marginWidth: WIDTH / (this.state.colomn + 2),
            marginHeight: HEIGHT / (this.state.row + 2)
        })
    }

    drawCircleGrid = () => {
        var children = [];
        for (let i = 0; i < this.state.row + 1; i++){
            for (let j = 0; j < this.state.colomn + 1; j++){
                children.push({x: this.gridX(j),y: this.gridY(i)});       
            }
        }

        return children

    }

    gridX = (i) => this.state.marginWidth * (i + 1) 

    gridY = (j) => this.state.marginHeight  * (j + 1)

    newGame = () => {
        var boxes = [];
        for (let i = 0; i < this.state.row; i++ ){
            boxes[i] = []
            for(let j = 0; j < this.state.colomn; j++){
                boxes[i][j] = new Box(this.gridX(j), this.gridY(i), this.state.marginWidth, this.state.marginHeight);
            }
        }
    }



    handleClick = () => {
        this.setState({
          color: Konva.Util.getRandomColor()
        });
    }

    render(){   
        const border = 5;
        const shadow = 5;

        const circles = this.drawCircleGrid().map((el,index)=>{
            return <Circle key={index} x={el.x} y={el.y} radius={10} fill="red" />
        }) 

        return(
            <div>

                <Stage width={WIDTH+border*2+shadow} height={HEIGHT+border*2+shadow}>
                    <Layer> 
                        <Rect
                            x={border}
                            y={border}
                            width={WIDTH+border}
                            height={HEIGHT+border}
                            fill={this.state.color}
                            shadowBlur={shadow}
                            stroke={'black'} 
                            strokeWidth={border}
                            onClick={this.handleClick}
                        >  
                        </Rect>

                        {circles}

                    </Layer>
                </Stage> 
            </div>
        )
    }
}
