import React, { Component } from 'react';
import {WIDTH, HEIGHT, COLORS} from './constants'
import { Stage, Layer, Rect, Text, Circle, Line } from 'react-konva';
import Konva from 'konva';

export default class BoxCanvas extends Component{

    state = {
        color: 'grey',
        row: 5,
        colomn: 6
    };


    handleClick = () => {
        this.setState({
          color: Konva.Util.getRandomColor()
        });
    }

    componentDidMount = () => {

    }



    render(){   

        console.log(COLORS.body)
        return(
            <div>

                <Stage width={WIDTH} height={HEIGHT}>
                    <Layer> 
                        <Rect
                            x={0}
                            y={0}
                            width={WIDTH}
                            height={HEIGHT}
                            fill={this.state.color}
                            shadowBlur={5}
                            stroke={'black'} 
                            strokeWidth={5}
                            onClick={this.handleClick}
                        >  
                        </Rect>
                        <Circle x={200} y={100} radius={10} fill="red" />
                    </Layer>
                </Stage> 
            </div>
        )
    }
}
